import React, { useState, useEffect } from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'
import DeleteButton from './DeleteButton'

const PirateList = (props) => {
    const [pirates, setPirates] = useState([])

    const imgStyle = {
        maxWidth: '100px'
    };

    const removeFromDom = pirateId => {
        setPirates(pirates.filter(pirate=> pirate._id != pirateId))
    }

    useEffect(() => {
        axios.get('http://localhost:8000/pirates')
            .then(res=> setPirates(res.data))
    }, [])


    return(
        <div className=''>
            {
            props.pirates.map( (pirate, index) =>
            <div key={index} className="d-flex justify-center">
                    <h1 className="text-2xl">{pirate.name}</h1>
                    <div>
                        <div>
                            <img style={imgStyle} src={pirate.image} />
                        </div>

                        <div>
                            <Link className="ms-3 btn btn-success" to={`/pirates/${pirate._id}`}>View</Link>
                            <DeleteButton pirateId={pirate._id}
                            successCall={()=> removeFromDom(pirate._id)} />
                        </div>
                    </div>
                </div>
            )}
        </div>
    )
}

export default PirateList
