import Main from './Views/Main';
import UpdatePirate from './Views/UpdatePirate';
import Details from './Views/Details';

function App() {
  return (
    <BrowserRouter>
      <div className='m-3'>
        <nav>
          <h1>Favorite Authors</h1>
        </nav>
        <Switch>
          {/* CREATE */}
          <Route exact path='/pirates/new'>
            <Link to="/pirates" className="btn btn-primary">Home</Link>
            <AuthorForm />
          </Route>

          {/* READ */}
          <Route exact path='/pirates'>
            <Link to="/pirates/new" className="btn btn-primary">Add Author</Link>
            <Main />
          </Route>

          {/* UPDATE */}
          <Route exact path='/pirates/:id'>
            <Link to="/pirates" className="btn btn-primary">Home</Link>
            <Details />
          </Route>

        </Switch>
      </div>
    </BrowserRouter>
  );
}

export default App;
