import React, { useState, useEffect } from 'react'
import { useParams, Link, useHistory } from 'react-router-dom'
import axios from 'axios'

const Details = (props) => {
    const [pirate, setPirate] = useState({})
    const { id } = useParams();
    const history = useHistory()

    useEffect(() => {
        axios.get('http://localhost:8000/pirates/' +id)
            .then(response => setPirate(response.data))
            .catch(err => console.error(err));
    }, []);

    const deleteHandler = () => {
        axios.delete('http://localhost:8000/pirates/' +id)
            .then(res => {
                console.log(res.data)
                history.push('/pirates')
            })
            .catch()
    }

    return (
        <div className="container">
            <nav>
                <h1>Name: {pirate.name}</h1>
            </nav>
            <div className="d-flex justify-content-around">
                <div style={'max-width: 200px'}>
                    <img src={pirate.image} alt=""/>
                    <h3>"{pirate.phrase}"</h3>
                </div>
                <div>
                    <h4 className="">About</h4>
                    <p className="ms-2">Position: {pirate.position}</p>
                    <p className="ms-2">Treasures: {pirate.treasures}</p>
                    <p className="ms-2">pegLeg: {pirate.pegLeg}</p>
                    <p className="ms-2">eyePatch: {pirate.eyePatch}</p>
                    <p className="ms-2">hookHand: {pirate.hookHand}</p>
                    <Link to={"/pirates/" + pirate._id + "/edit"} className="btn btn-primary" >
                        Edit
                    </Link>
                    <button className="btn btn-danger ms-3" onClick={deleteHandler}>Delete</button>
                </div>
            </div>
        </div>
    )
}

export default Details;
